const heading=document.querySelector(".headingrow");
options={rootMargin:"-100px"},
observer= new IntersectionObserver((function(e,o){
    e.forEach(e=>{if(!e.isIntersecting){
        document.getElementById("nav").style.backgroundColor="black";
        for(var o=1;o<=5;o++)
            document.getElementById("li"+o).style.color="white"
        }   
    if(e.isIntersecting){
        document.getElementById("nav").style.backgroundColor="rgba(0,0,0, 0)";for(o=1;o<=5;o++)document.getElementById("li"+o).style.color=""
    }
})
}),options),observer.observe(heading);

